/**
 * INPUT
 *   $json.email   â€“ the prospectâ€™s eâ€‘mail address
 *
 * OUTPUT
 *   Keeps the original fields and adds:
 *     â€¢ companyDomain   â†’  "acme.com"
 *     â€¢ companyWebsite  â†’  "https://acme.com"
 *
 *   If the address is a public mailbox (gmail, outlook, etc.) both fields are null.
 */

const email = ($json.email || '').trim().toLowerCase();

// â”€â”€ 1. extract â€œdomainâ€ part after @ â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const domainMatch = email.match(/@([^@]+)$/);
if (!domainMatch) {
  // not a valid eâ€‘mail â†’ just return the item unchanged
  return $json;
}
let domain = domainMatch[1];

// â”€â”€ 2. strip common mail subâ€‘domains (mail., email., m.) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
domain = domain.replace(/^(mail|email|m)\./, '');

// â”€â”€ 3. detect throwâ€‘away & public providers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const publicProviders = [
  //   *** expand / tweak as needed ***
  'gmail.com','googlemail.com','yahoo.com','ymail.com','aol.com','outlook.com',
  'hotmail.com','live.com','icloud.com','me.com','msn.com','protonmail.com',
  'gmx.com','mail.com','pm.me','yandex.com','zoho.com'
];
if (publicProviders.includes(domain)) {
  $json.companyDomain  = null;
  $json.companyWebsite = null;
  return $json;
}

// â”€â”€ 4. reduce multiâ€‘level domains to 2ndâ€‘level + TLD if obvious â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
//   e.g. "john@team.salesforce.co.uk"  â†’  "salesforce.co.uk"
const parts = domain.split('.');
if (parts.length > 2) {
  const tld = parts.slice(-2).join('.');
  const sld = parts.slice(-3, -2)[0];      // secondâ€‘level candidate
  // simple heuristic: drop the first piece if itâ€™s short (subâ€‘domainâ€‘y)
  if (parts[0].length <= 4) domain = `${sld}.${tld}`;
}

// â”€â”€ 5. attach to item â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
$json.companyDomain  = domain;
$json.companyWebsite = `https://${domain}`;

return $json;

/**
 * INPUT
 *   $json.firstName, $json.lastName, $json.companyName
 *   $json.searchResponse.organic  â€¦ searchâ€‘API results
 *
 * OUTPUT
 *   passes through everything + reliable linkedinUrl (null if no confident hit)
 */

const first  = ($('Required Fields').item.json.firstname  || '').trim();
const last   = ($('Required Fields').item.json.lastname   || '').trim();
const company= ($('Required Fields').item.json.company|| '').trim();
const results= $json.organic_results || [];

const normalize = str =>
  str.toLowerCase()
     .replace(/[^\w\s]/g, '')                // remove punctuation
     .replace(/\s+/g, ' ')
     .trim();

const nFirst   = normalize(first);
const nLast    = normalize(last);
const nCompany = normalize(company);

// lightweight Diceâ€™s coefficient similarity on bigrams
const sim = (a, b) => {
  if (!a || !b) return 0;
  const bigrams = s => new Set([...s].slice(0,-1).map((_,i)=>s.slice(i,i+2)));
  const A = bigrams(a), B = bigrams(b);
  const overlap = [...A].filter(x => B.has(x)).length;
  return (2 * overlap) / (A.size + B.size || 1);
};

let best = null;
let bestScore = 0;

// evaluate up to first 10 organic results
for (const r of results.slice(0, 10)) {
  const link = r.link || '';
  if (!/^https:\/\/(www\.)?linkedin\.com\/in\//.test(link)) continue; // skip nonâ€‘profile URLs

  const text = normalize(`${r.title} ${r.snippet}`);

  // name score: need both first & last present
  const nameScore = sim(text, `${nFirst} ${nLast}`);

  // company score: company may appear in snippet OR LinkedInâ€™s hyphenated title
  const companyScore = nCompany ? sim(text, nCompany) : 1;

  // harmonic mean favours both being high
  const combined = (2 * nameScore * companyScore) / (nameScore + companyScore || 1);

  if (combined > bestScore) {
    bestScore = combined;
    best = link.split('?')[0]; // drop any trackers
  }
}

// require â‰¥0.50 overall confidence  (adjust as needed)
$json.linkedinUrl = bestScore >= 0.50 ? best : null;
$json.linkedinMatchScore = bestScore;      // optional, handy for QA

return $json;

/**
 * INPUT
 *   $json â”€ the LinkedIn profile object (one profile per item)
 *
 * OUTPUT
 *   { json: { â€¦flattened fieldsâ€¦ } }   â† required when node runs
 *   in â€œRun once for each itemâ€ mode
 *
 * CHANGES
 *   â€¢ Prospect company is pulled from the *current* item instead of an
 *     external node, so the code is self-contained.
 *   â€¢ All optional properties accessed with optional-chaining (no crashes).
 *   â€¢ Always returns a single item ( { json: â€¦ } ).
 */

// ---------------------------------------------------------------------------
// 0. get the company weâ€™re looking for
//    (try several possible property names to stay flexible)
const prospectCompany =
  ($json.company ??
   $json.companyName ??
   $json['Management Company'] ??
   '').toString().trim();

// ---------------------------------------------------------------------------
// Helper utilities
const normalize = str => str
  .toLowerCase()
  .replace(/[^\w\s]/g, '')                           // remove punctuation
  .replace(/\b(inc|llc|ltd|co|corp(oratio)?n?)\b/g, '') // drop legal suffixes
  .replace(/\s+/g, ' ')
  .trim();

const similarity = (a, b) => {
  if (!a || !b) return 0;
  const bigrams = s =>
    new Set([...s].slice(0, -1).map((_, i) => s.slice(i, i + 2)));
  const A = bigrams(a), B = bigrams(b);
  const overlap = [...A].filter(x => B.has(x)).length;
  return (2 * overlap) / (A.size + B.size || 1);
};

// ---------------------------------------------------------------------------
// 1. basic profile block
const out = {
  name: ($json.fullName || '').trim(),
  linkedinUrl: $json.linkedinUrl,
  headline: $json.headline,
  addressWithoutCountry: $json.addressWithoutCountry,
  about: $json.about,
  followers: $json.followers,
  email: $json.email,
  mobileNumber: $json.mobileNumber,
};

// ---------------------------------------------------------------------------
// 2. collect current roles (caption contains â€œPresentâ€)
const currentRoles = ($json.experiences ?? [])
  .filter(r => (r.caption ?? '').includes('Present'))
  .map(r => {
    // company name = text before the first Â· or the whole subtitle
    const company = ((r.subtitle ?? '').split('Â·')[0] || r.subtitle || '').trim();
    const description =
      r.subComponents?.[0]?.description?.[0]?.text ?? null;

    return {
      company,
      title: r.title ?? null,
      description,
      tenure: r.caption ?? null,
      companyLink: r.companyLink1 ?? null,
      score: similarity(normalize(company), normalize(prospectCompany)),
    };
  });

// ---------------------------------------------------------------------------
// 3. decide primary vs secondary role
currentRoles.sort((a, b) => b.score - a.score); // best match first
let [primary, ...secondaries] = currentRoles;

if (primary && primary.score < 0.40 && prospectCompany) {
  // No good match â€“ still use first as primary but mark score 0
  primary.score = 0;
}

// ---------------------------------------------------------------------------
// 4. write Primary Job â€¦ fields
if (primary) {
  out['Primary Job Company']              = primary.company;
  out['Primary Job Company LinkedIn URL'] = primary.companyLink;
  out['Primary Job Title']                = primary.title;
  out['Primary Job Description']          = primary.description;
  out['Primary Job Tenure']               = primary.tenure;
}

// ---------------------------------------------------------------------------
// 5. write Secondary Job N â€¦ fields
secondaries.forEach((r, idx) => {
  const n = idx + 1;
  out[`Secondary Job ${n} Company`]              = r.company;
  out[`Secondary Job ${n} Company LinkedIn URL`] = r.companyLink;
  out[`Secondary Job ${n} Title`]                = r.title;
  out[`Secondary Job ${n} Description`]          = r.description;
  out[`Secondary Job ${n} Tenure`]               = r.tenure;
});

// ---------------------------------------------------------------------------
// 6. return a SINGLE item (required in â€œRun once for each itemâ€ mode)
return { json: out };

